package String;

public class Contains {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        String s="my name is vikas kaushal";
        System.out.println(s.contains("vikas"));
        System.out.println(s.contains("vikas kaushal"));
        System.out.println(s.contains("vikaskaushal"));
        System.out.println(s.contains("hello"));
        System.out.println(s.contains("my name is"));
        System.out.println(s.contains("My"));
	}

}
